import React, { useState, useEffect } from "react";
import "./EditRestaurant.css";
import { useNavigate, useParams } from "react-router-dom";
import { useForm } from "react-hook-form";

export default function EditRestaurant() {
  const { restaurantId } = useParams();

  const [restaurant, setRestaurant] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetch(`http://localhost:5000/restaurant/${restaurantId}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error("Network response was not ok");
        }
        return response.json();
      })
      .then((data) => {
        setRestaurant(data);
        setLoading(false);
      })
      .catch((error) => {
        setError(error);
        setLoading(false);
      });
  }, []);

  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    defaultValues: {
      name: restaurant.name,
      cuisine: restaurant.cuisine,
      address: restaurant.address,
      location: restaurant.location,
      ratings: restaurant.ratings,
      image: restaurant.image,
    },
  });

  const onSubmit = async (data) => {
    const response = await fetch(
      `http://localhost:5000/restaurantupdate/${restaurantId}`,
      {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(data),
      }
    );
    if (response.ok) {
      const updatedRestaurant = await response.json();
      console.log("Restaurant details updated:", updatedRestaurant);
      navigate("/admin/managerestaurant");
    } else {
      const errorData = await response.json();
      console.error("Error updating restaurant details:", errorData.error);
    }
  };

  return (
    <div className="container">
      <div className="row justify-content-center">
        <div className="col-md-6">
          <div className="edit-restaurant-container">
            <form onSubmit={handleSubmit(onSubmit)} className="signup-form">
              {/* ... (rest of the form fields) ... */}
              <button type="submit" className="btn btn-primary" value="Update">
                Update
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
