import React from "react";
import { useAuth } from "../Context/AuthContext";
const logoImage = require('./logo.png');

const styles = {
  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    backgroundColor: "black",
    color: "#fff",
    padding: "10px 0",
  },
  logoImage:{
    width:"220px",

  },
  nav: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    margin: "0 auto",
    gap: "700px",
  },
  logo: {
    fontSize: "24px",
  },
  ul: {
    listStyle: "none",
    padding: 0,
    display: "flex",
  },
  li: {
    marginRight: "20px",
  },
  a: {
    color: "#fff",
    textDecoration: "none",
  },
};

const Header = () => {
  const { state, dispatch } = useAuth();

  return (
    <header style={styles.header}>
      <nav style={styles.nav}>
      <img src={logoImage} alt="Hungary Hub Logo" style={styles.logoImage} />
        <ul style={styles.ul}>
          <li style={styles.li}>
            <a href="/" style={styles.a}>
              Home
            </a>
          </li>
          <li style={styles.li}>
            <a href="/restlist" style={styles.a}>
              RestaurantList
            </a>
          </li>
          <li style={styles.li}>
            <a href="/about" style={styles.a}>
              About
            </a>
          </li>
          <li style={styles.li}>
            <a href="/contact" style={styles.a}>
              Contact
            </a>
          </li>

          {state.isAuthenticated ? (
            <>
              <li style={styles.li}>
                <a href="/paypalpayment" style={styles.a}>
                  PayPal
                </a>
              </li>

              <li style={styles.li}>
                <a href="/userprofile" style={styles.a}>
                  {state.user.fname + "," + state.user.lname}
                </a>
              </li>
              <li style={styles.li}>
                <a href="/logout" style={styles.a}>
                  Logout
                </a>
              </li>
            </>
          ) : (
            <>
              <li style={styles.li}>
                <a href="/signup" style={styles.a}>
                  Register
                </a>
              </li>
              <li style={styles.li}>
                <a href="/login" style={styles.a}>
                  Login
                </a>
              </li>
            </>
          )}
        </ul>
      </nav>
    </header>
  );
};

export default Header;
