import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../components/css/SignupForm.css';

const AddUser = () => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    username: '',
    fname: '',
    lname: '',
    phoneNumber: '',
    email: '', // Add email field
    password: '', // Add password field
  });

  const [addUserStatus, setAddUserStatus] = useState(null);

  const [errors, setErrors] = useState({
    username: '',
    fname: '',
    lname: '',
    phoneNumber: '',
    email: '', // Add email field
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const newErrors = {};
    if (formData.username.trim() === '') {
      newErrors.username = 'Username is required';
    }
    if (formData.fname.trim() === '') {
      newErrors.fname = 'FirstName is required';
    }
    if (formData.lname.trim() === '') {
      newErrors.lname = 'LastName is required';
    }
    const phoneRegex = /^\d{10}$/; // Regular expression for a 10-digit phone number
    if (!formData.phoneNumber.match(phoneRegex)) {
      newErrors.phoneNumber = 'Invalid phone number (10 digits)';
    }
    if (formData.email.trim() === '') {
      newErrors.email = 'Email is required'; // Add email validation
    }
    // Add password validation if needed

    setErrors(newErrors);

    // Check if there are any errors before making the API call
    if (Object.keys(newErrors).length === 0) {
      try {
        await axios.post('http://localhost:5000/api/add-user', formData);
        console.log('User added successfully');
        setAddUserStatus('success');
        navigate('/admin/manageusers');
      } catch (error) {
        console.error('Error adding user:', error);
        setAddUserStatus('error');
      }
    }
  };

  return (
    <div className="container">
      {/* ... (rest of the component remains the same) ... */}
    </div>
  );
};

export default AddUser;
