import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { Link } from 'react-router-dom';

const Menu = () => {
  const { restaurantId } = useParams();
  const [menu, setMenu] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [review, setReview] = useState('');
  const [rating, setRating] = useState(0);
  const [selectedRating, setSelectedRating] = useState('1');

  const handleReviewSubmit = async (e) => {
    e.preventDefault();

    if (!review || !selectedRating) {
      console.log('Please fill in both review and rating fields.');
      return;
    }

    try {
      const response = await fetch('http://localhost:5000/api/reviews', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          restaurantId,
          review,
          rating: parseInt(selectedRating, 10), // Convert the selectedRating to an integer
        }),
      });

      console.log('Response:', response);

      if (!response.ok) {
        throw new Error('Failed to submit review');
      }

      setReview('');
      setSelectedRating('1'); // Reset to the initial value
      console.log('Review submitted successfully');
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    fetch(`http://localhost:5000/menu/${restaurantId}`)
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        setMenu(data);
        setLoading(false);
      })
      .catch((error) => {
        setError(error);
        setLoading(false);
      });
  }, [restaurantId]);

  return (
    <div className="container">
      <h2>Menu for Restaurant</h2>
      <div className="row">
        <div className="col-md-8">
          {loading ? (
            <p>Loading menu...</p>
          ) : error ? (
            <p>Error: {error.message}</p>
          ) : Array.isArray(menu) && menu.length > 0 ? (
            <div className="menu-items">
              {menu.map((item, index) => (
                <div key={index} className="menu-item mb-4">
                  <div className="row">
                    <div className="col-md-4">
                      <img
                        src={item.image}
                        alt={item.name}
                        className="img-fluid menu-item-image"
                      />
                    </div>
                    <div className="col-md-8">
                      <h3>{item.name}</h3>
                      <p>{item.description}</p>
                      <p>Price: ${item.price}</p>
                      <Link
                        to={`/menu/${restaurantId}/${index}`}
                        className="view-details-link"
                      >
                        <button className="view-details-button">View Details</button>
                      </Link>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <p>No menu items to display.</p>
          )}
        </div>

        <div className="col-md-4">
          <div className="review-form-container">
            <h2>Write a Review</h2>
            <form onSubmit={handleReviewSubmit}>
              <textarea
                className="form-control review-input"
                placeholder="Write your review..."
                value={review}
                onChange={(e) => setReview(e.target.value)}
                required
              />
              <select
                id="rating"
                className="form-control rating-dropdown mt-3"
                value={selectedRating}
                onChange={(e) => setSelectedRating(e.target.value)}
                required
              >
                {[1, 2, 3, 4, 5].map((value) => (
                  <option key={value} value={value}>
                    {value}
                  </option>
                ))}
              </select>
              <button className="btn btn-primary mt-3" type="submit">
                Submit Review
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Menu;
