import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate
import './css/SignupForm.css';

const DeliveryAddress = () => {


    const navigate = useNavigate();
    const [formData, setFormData] = useState({
      username: '',

     phoneNumber: '',
    });
    const [registrationStatus, setRegistrationStatus] = useState(null);


    const [errors, setErrors] = useState({
      username: '',


      phoneNumber: '',
    });

    const handleInputChange = (e) => {
      const { name, value } = e.target;
      setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
      e.preventDefault();

      const newErrors = {};
      if (formData.username.trim() === '') {
        newErrors.username = 'delivery address is required';
        setErrors(newErrors);
        return;
      }

      const phoneRegex = /^\d{10}$/; // Regular expression for a 10-digit phone number
      if (!formData.phoneNumber.match(phoneRegex)) {
        newErrors.phoneNumber = 'Invalid phone number (10 digits)';
        setErrors(newErrors);
        return;
      }

      navigate('/thankyou'); 
    };

    return (
      <div className="signup-form-container">

        <form className="signup-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="username">Delivery address:</label>
            <span>  <input
              type="text"
              id="username"
              name="username"
              value={formData.username}
              onChange={handleInputChange}
            /></span>

            <span className="error-message">{errors.username}</span>
          </div>
          <div className="form-group">
            <label htmlFor="fname">Suit No:</label>
            <input
              type="text"
              id="fname"
              name="fname"
              value={formData.fname}
              onChange={handleInputChange}
            />
                </div>


          <div className="form-group">
            <label htmlFor="phoneNumber">Phone Number:</label>
            <input
              type="tel"
              id="phoneNumber"
              name="phoneNumber"
              value={formData.phoneNumber}
              onChange={handleInputChange}
            />
            <span className="error-message">{errors.phoneNumber}</span>
          </div>
          <div>
        <label htmlFor="specialInstructions">Special Instructions For Driver:</label>
        <textarea
          id="specialInstructions"
          name="specialInstructions"
          value={formData.specialInstructions}
          onChange={handleInputChange}
        />
      </div>

          <center><button className='btnsubmit signupbtn' type="submit">Confirm</button></center>
        </form>
      </div>
    );
  };


export default DeliveryAddress;
